<?php require 'connect.php'; $data = json_decode(file_get_contents("php://input"), true); $id = $data['id']; $alamat = $data['alamat']; $sql = "UPDATE users SET alamat=? WHERE id=?"; $stmt = $conn->prepare($sql); $stmt->bind_param("si", $alamat, $id); if ($stmt->execute()) { echo json_encode(["message" => "Alamat berhasil diperbarui"]); } else { echo json_encode(["message" => "Gagal memperbarui alamat"]); } ?>