<?php error_reporting(E_ALL); ini_set('display_errors', 1); header("Content-Type: application/json"); include "connect.php"; $data = json_decode(file_get_contents("php://input"), true); if (!$data) { echo json_encode(["status" => "error", "message" => "Invalid input"]); exit; } $id = $data["id"]; $name = $data["name"]; $email = $data["email"]; $alamat = $data["alamat"]; $no_telepon = $data["no_telepon"]; $jenis_kelamin = $data["jenis_kelamin"]; $tanggal_lahir = $data["tanggal_lahir"]; $sql = "UPDATE users SET name = ?, email = ?, alamat = ?, no_telepon = ?, jenis_kelamin = ?, tanggal_lahir = ? WHERE id = ?"; $stmt = $conn->prepare($sql); $stmt->bind_param("ssssssi", $name, $email, $alamat, $no_telepon, $jenis_kelamin, $tanggal_lahir, $id); if ($stmt->execute()) { echo json_encode(["status" => "success", "message" => "User updated successfully"]); } else { echo json_encode(["status" => "error", "message" => "Update failed", "error" => $stmt->error]); } $stmt->close(); $conn->close();